mapper.update:
update storyDetail
set map_id=#{mapId,jdbcType=INTEGER} , story_title=#{storyTitle,jdbcType=VARCHAR} , story_description=#{storyDescription,jdbcType=VARCHAR} ,
story_last_modified_date=#{storyLastModifiedDate,jdbcType=TIMESTAMP} , cood_x=#{coodX,jdbcType=DOUBLE} , cood_y=#{coodY,jdbcType=DOUBLE},
color_pick=#{colorPick,jdbcType=VARCHAR}, comments=#{comments,jdbcType=VARCHAR}
where story_id=#{storyId,jdbcType=INTEGER}
